import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import NeuroTypeDropdown from './components/NeuroTypeDropdown';
import UserInput from './components/UserInput';
import SummaryDisplay from './components/SummaryDisplay';

const App: React.FC = () => {
    const [neuroType, setNeuroType] = useState('');
    const [userInput, setUserInput] = useState('');
    const [summary, setSummary] = useState('');

    useEffect(() => {
        // Fetch or generate the summary based on `neuroType` and `userInput`
        const fetchSummary = async () => {
            // Simulate a network request
            await new Promise(resolve => setTimeout(resolve, 500));
            setSummary(`Summary based on input: ${userInput} and type: ${neuroType}`);
        };

        if (userInput && neuroType) {
            fetchSummary();
        }
    }, [userInput, neuroType]);

    return (
        <div className="App">
            <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <h1 className="text-2xl font-bold text-blue-500">Hello World</h1>
                <p>
                    Edit <code>src/App.tsx</code> and save to reload.
                </p>
                <a
                    className="App-link"
                    href="https://reactjs.org"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Learn React
                </a>
            </header>
            <div className="p-4">
                <h2 className="text-lg font-bold">Neurodivergence Input Form</h2>
                <NeuroTypeDropdown onChange={(e) => setNeuroType(e.target.value)} />
                <UserInput value={userInput} onChange={(e) => setUserInput(e.target.value)} />
                <SummaryDisplay summary={summary} />
            </div>
        </div>
    );
};

export default App;
