import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './Header';
import InputForm from './InputForm';
import SummaryDisplay from './components/SummaryDisplay';

const App: React.FC = () => {
    const [neuroType, setNeuroType] = useState('');
    const [userInput, setUserInput] = useState('');
    const [summary, setSummary] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        if (!userInput || !neuroType) {
            setSummary('');
            return;
        }
        fetchSummaryBasedOnType();
    }, [userInput, neuroType]);

    const fetchSummaryBasedOnType = async () => {
        setIsLoading(true);
        try {
            const response = await fetch(`http://localhost:8000/summarize/${neuroType.toLowerCase()}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ text: userInput })
            });
            if (!response.ok) throw new Error('Failed to fetch');
            const data = await response.json();
            setSummary(data.summary);
        } catch (error) {
            console.error('Error fetching summary based on type:', error);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="App">
            <Header />
            <div className="content">
                <div className="input-area">
                    <InputForm neuroType={neuroType} setUserInput={setUserInput} setNeuroType={setNeuroType} fetchSummary={fetchSummaryBasedOnType} />
                </div>
                <div className="output-area">
                    {isLoading ? <p>Loading summary...</p> : <SummaryDisplay summary={summary} />}
                </div>
            </div>
        </div>
    );
};

export default App;
